INSERT INTO places (id, name, description, latitude, longitude)
VALUES (1, 'Красная площадь', 'Красная площадь — центральная площадь Москвы, расположенная между Кремлем и историческим торговым центром ГУМ. Она является символом России и важным историческим и культурным центром. Площадь имеет прямоугольную форму и окружена знаменитыми зданиями, такими как Храм Василия Блаженного с его яркими куполами, Государственный исторический музей и мавзолей Ленина. Красная площадь служит местом проведения различных мероприятий, парадов и праздников, а также привлекает туристов со всего мира благодаря своей архитектурной красоте и историческому значению.', 55.753544, 37.621202);
